import React, { useState } from "react";
import './Styles.css'
import axios from "axios";
import Vehicle from "../../dao/Vehicle";

const Update = (props) => {
    const [showForm, setShowForm] = useState(true);
    const [value, setValue] = useState('');

    const changeValue = (e) => {
        const {name, value} = e.target
        setValue(v=>({...v, [name]: value}))
    }
    
    const CompleteFields = async () => {
        const  findUrl = `http://localhost:8080/api/unowsoltions/vehicles/find?id=${props.id}`;
        const headers = {
          'Content-Type': 'application/json'
        };
        const response = await axios.get(findUrl, { headers })
        const data = response.data;
        value.carBrand = data.brand;
        value.carModel = data.model;
        value.carPlate = data.licensePlate;
        value.carColor = data.color;
        value.carMake = data.year;
      }

    const update = (e) => {
        e.preventDefault();
        let updateUrl = 'http://localhost:8080/api/unowsoltions/vehicles/insert';
        if(props.typeRequest === "Update") {
            CompleteFields();
            updateUrl=`http://localhost:9090/api/unowsoltions/vehicles/update/${props.id}`;
        }
        const vehicle = new Vehicle(value.carBrand, value.carModel, value.carPlate, value.carColor, value.carMake);

        const body = {
            brand : vehicle.brand,
            model : vehicle.model,
            licensePlate : vehicle.licensePlate,
            color : vehicle.carColor,
            year : vehicle.make
        }

        if(updateUrl.includes('update')) {
            
            axios.put(updateUrl,  content, { headers })
            .then(response => {
            alert(response.data);
            if(response.data.includes('')){
                setShowForm(false)
            }
            })
        }
        else {

        }
    }
    return (
        <form class="update" onSubmit={update}>
        <label for="car-brand"><span>Marca</span>
            <input onChange={changeValue} name="carBrand" value={value.carBrand || ''} id="car-brand" type="text" />
        </label>

        <label for="car-model"><span>Modelo</span>
            <input onChange={changeValue} name="carModel" value={value.carModel || ''} id="car-model" type="text" />
        </label>

        <label for="car-plate"><span>Matricula</span>
            <input onChange={changeValue} name="carPlate" value={value.carPlate || ''} id="car-plate" type="text" />
        </label>

        <label for="car-make"><span>AÃ±o</span>
            <input onChange={changeValue} name="carMake" value={value.carMake || ''} id="car-make" type="text" />
        </label>

        <label for="car-color"><span>Color</span>
            <input onChange={changeValue} name="carColor" value={value.carColor || ''} id="car-color" type="text" />
        </label>

        <div className="update-btn-container">
            
            <input type="submit" value="Actualizar" />

            <input type="reset" value="Limpiar" />
            
        </div>
    </form>
    )
}

export default Update;
