import React, { useState } from "react";
import './Styles.css';
import Lupa from "../../images/lupa.svg";

const Header = () => {
    const [searchTerm, setSearchTerm] = useState(""); 

    useEffect(() => {
        const intervalId = setInterval(() => {
            if(searchTerm === ""){
                localStorage.setItem('searchTerm', '');
            }
        }, 1000);
    
        return () => clearInterval(intervalId);
    }, [searchTerm]);

    const changeTerm = e => {
        setSearchTerm(e.target.value);
    }

    const storeTerm = (e) => {
        e.preventDefault();
        console.log(searchTerm);
        setSearchTerm(prevSearchTerm => {
          localStorage.setItem('searchTerm', prevSearchTerm); // Store the previous searchTerm
          return prevSearchTerm; // Return the previous searchTerm
        });
      };

    return(
        <header>
            <div className="logo-container">
                <span className="logo"></span>
            </div>
            <div className="search-area">
                <input type="text" 
                className="search-bar"
                onChange={changeTerm}
                value={searchTerm} />
                <button className="search-btn"><img alt="Lupa" src={Lupa} onClick={storeTerm}/></button>
            </div>
            <button className="add-vehicle" title="Agregar vehiculo">+</button>
        </header>
    )
}

export default Header;
