export default class MsgUtils {
    static CustomMessage(msgArray) {
        console.log('NO FORMAT : ' + msgArray);
        let finalMsg = '';
        if (!Array.isArray(msgArray)) {
            if(msgArray.includes('actualizado') || msgArray.includes('insertado') ){
                finalMsg=msgArray;
            }
        }
        msgArray.forEach(obj => {
            finalMsg += obj.msg + '\n';
        });
        console.log('WITH FORMAT : ' + finalMsg);
        return finalMsg;
    }
}
