export default class MsgUtils {
    static CustomMessage(msgArray) {
        console.log(msg);
        let finalMsg = '';
        if (!Array.isArray(msgArray)) {
            if(msgArray.includes('actualizado') || msg.includes('insertado') ){
                finalMsg=msgArray;
            }
        }
        msgArray.forEach(obj => {
            if (obj.hasOwnProperty('msg')) {
                finalMsg += obj.msg + '\n';
            }
        });
        return finalMsg;
    }
}
