import React, { useState } from "react";
import './Styles.css'

const Update = () => {
    const [value, setValue] = useState('');
    const [brand, setBrand] = useState('');
    const [model, setModel] = useState('');
    const [licensePlate, setLicensePlate] = useState('');
    const [color, setColor] = useState('');
    const [year, setYear] = useState('');

    const cambiarvalue = (e) => {
        const {name, value} = e.target
        setValue(v=>({...v, [name]: value}))
    }
    return (
        <form class="update">
        <label for="car-brand"><span>Marca</span>
            <input onChange={cambiarvalue} name="carBrand" value={value.carBrand || ''} id="car-brand" type="text" />
        </label>

        <label for="car-model"><span>Modelo</span>
            <input onChange={cambiarvalue} name="carModel" value={value.carModel || ''} id="car-model" type="text" />
        </label>

        <label for="car-plate"><span>Matricula</span>
            <input onChange={cambiarvalue} name="carPlate" value={value.carPlate || ''} id="car-plate" type="text" />
        </label>

        <label for="car-make"><span>AÃ±o</span>
            <input onChange={cambiarvalue} name="carMake" value={value.carMake || ''} id="car-make" type="text" />
        </label>

        <label for="car-color"><span>Color</span>
            <input onChange={cambiarvalue} name="carColor" value={value.carColor || ''} id="car-color" type="text" />
        </label>
        <div className="update-btn-container">
            
            <input type="submit" value="Actualizar" />

            <input type="reset" value="Limpiar" />
            
        </div>
    </form>
    )
}

export default Update;
