import React, { useEffect, useState } from "react";
import Header from '../components/header/MainHeader';
import Landing from "../components/landing/Landing";
import Update from "../components/update/Update";
import PopupMessage from "../components/dialog/PopupMessage";

const HomePage = () => {
    const [showInsert, setShowInsert] = useState(false); 
    const [showUpdate, setShowUpdate] = useState(false); 
    const [showPopup, setShowPopup] = useState(false);
    const [popupMsg, setPopupMsg] = useState("");

    const handleInsert = (showFormInsert) => {
        setShowInsert(showFormInsert);
        console.log(showFormInsert);
    }

    useEffect(() => {
        setShowPopup(false);
        localStorage.setItem('showPopup', false);
      }, []);
      
    useEffect(() => {
        const intervalId = setInterval(() => {
        setShowPopup(localStorage.getItem('showPopup'));
        setPopupMsg(localStorage.getItem('popupMsg'));
        setShowUpdate(localStorage.getItem('update'));
        localStorage.setItem('update', false);
        }, 1000);
    
        return () => clearInterval(intervalId);
    }, []);
    return (
        <>
        <Header registerVisible={handleInsert}/>
        {showInsert && <Update typeRequest="insert"/>}
        {showPopup && <PopupMessage message={popupMsg} onClose={() => setShowPopup(false)} />}
        {!showInsert && !showUpdate && <Landing />}
        </>
    )
}

export default HomePage;
