import React, { useEffect, useState } from "react";
import './Styles.css';
import image from '../../images/honda.jpg';
import Update from "../update/Update";
import Detail from "../detail/Detail";

const Card = (props) => {
    const [showForm, setShowForm] = useState(false);
    const [showDetail, setShowDetail] = useState(false);
    return (
        <div>
         {showForm && <Update typeRequest="update" id={props.vehicle.id}/>}
         {showDetail && <Detail vehicle={props.vehicle}/>}
        <div className="card">
            <div className="img-container" onClick={() => setShowDetail(true)}>
                <img src={image} alt="Imagen de vehiculo" />
            </div>
            <div className="info-container">
                <h1 onClick={() => <Detail vehicle={props.vehicle}/>}>{props.vehicle.model}</h1>
                <h4 onClick={() => <Detail vehicle={props.vehicle}/>}>{props.vehicle.year} </h4>
                <div className="btn-container">
                    <button className="info-btn" onClick={() => setShowForm(true)}>Actualizar</button>
                    <button className="info-btn">Eliminar</button>
                </div>
            </div>
        </div>
        </div>
    )
}

export default Card;
