import React, { useState } from 'react';

const Pagination = (props) => {
  const { size } = props;
  const perPage = 2;
  const [indexPage, setIndexPage] = useState(1); // Using state to track indexPage

  const handlePreviousClick = () => {
    if (indexPage === 1) return; // No previous page if already on the first page
    setIndexPage(indexPage - 1);
  };

  const handleNextClick = () => {
    if (indexPage === Math.ceil(size / perPage)) return; // No next page if already on the last page
    setIndexPage(indexPage + 1);
  };

  return (
    <div>
      <button value="anterior" onClick={handlePreviousClick}>anterior</button>
      <p>{indexPage}</p>
      <button value="siguiente" onClick={handleNextClick}>siguiente</button>
    </div>
  );
};

export default Pagination;
