import React, { useEffect, useState } from "react";
import './Styles.css';
import image from '../../images/honda.jpg';
import Update from "../update/Update";

const Card = (props) => {
    const [showForm, setShowForm] = useState(true);
    return (
        <div>
         {showForm && <Update typeRequest="update"/>}
        <div className="card">
            <div className="img-container">
                <img src={image} alt="Imagen de vehiculo" />
            </div>
            <div className="info-container">
                <h1>{props.model}</h1>
                <h4>{props.year} </h4>
                <div className="btn-container">
                    <button className="info-btn" onClick={() => setShowForm(true)}>Actualizar</button>
                    <button className="info-btn">Eliminar</button>
                </div>
            </div>
        </div>
        </div>
    )
}

export default Card;
